import React, { useState, useEffect } from 'react';
import { Calendar, Users, Vote, Settings, Plus, Edit2, Trash2, Eye, Download, Upload, Check, X, AlertCircle, Bitcoin, Key, UserPlus, Shield, Activity } from 'lucide-react';

const AdminDashboard = () => {
  const [activeTab, setActiveTab] = useState('elections');
  const [elections, setElections] = useState([]);
  const [users, setUsers] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [modalType, setModalType] = useState('');
  const [selectedElection, setSelectedElection] = useState(null);
  const [loading, setLoading] = useState(false);

  // Form states
  const [electionForm, setElectionForm] = useState({
    title: '',
    description: '',
    startDate: '',
    endDate: '',
    coinjoinTrigger: 10,
    coinjoinEnabled: true,
    maxVotersAllowed: '',
    votingMethod: 'single',
    blockchainNetwork: 'testnet'
  });

  const [candidateForm, setCandidateForm] = useState({
    firstName: '',
    lastName: '',
    party: '',
    biography: ''
  });

  const [userForm, setUserForm] = useState({
    firstName: '',
    lastName: '',
    email: '',
    taxCode: '',
    dateOfBirth: '',
    phoneNumber: ''
  });

  // Mock data
  useEffect(() => {
    // Carica dati mock
    setElections([
      {
        id: '1',
        title: 'Elezione Comunale 2025',
        description: 'Elezione del sindaco e consiglio comunale',
        startDate: '2025-03-01',
        endDate: '2025-03-02',
        status: 'draft',
        coinjoinTrigger: 10,
        coinjoinEnabled: true,
        candidates: [
          { id: '1', firstName: 'Mario', lastName: 'Rossi', party: 'Partito A', bitcoinAddress: 'bc1qxy2kgdygjrsqtzq2n0yrf2493p83kkfjhx0wlh' },
          { id: '2', firstName: 'Laura', lastName: 'Bianchi', party: 'Partito B', bitcoinAddress: 'bc1qxy2kgdygjrsqtzq2n0yrf2493p83kkfjhx0wlh' }
        ],
        whitelist: []
      }
    ]);

    setUsers([
      { id: '1', firstName: 'Giovanni', lastName: 'Verdi', email: 'g.verdi@email.com', taxCode: 'VRDGNN80A01H501Z', status: 'active', isVerified: true },
      { id: '2', firstName: 'Anna', lastName: 'Neri', email: 'a.neri@email.com', taxCode: 'NRANNA85M45H501X', status: 'active', isVerified: false }
    ]);
  }, []);

  const openModal = (type, data = null) => {
    setModalType(type);
    setShowModal(true);
    if (data) {
      if (type === 'editElection') {
        setElectionForm(data);
        setSelectedElection(data);
      }
    }
  };

  const closeModal = () => {
    setShowModal(false);
    setModalType('');
    setSelectedElection(null);
    // Reset forms
    setElectionForm({
      title: '',
      description: '',
      startDate: '',
      endDate: '',
      coinjoinTrigger: 10,
      coinjoinEnabled: true,
      maxVotersAllowed: '',
      votingMethod: 'single',
      blockchainNetwork: 'testnet'
    });
    setCandidateForm({
      firstName: '',
      lastName: '',
      party: '',
      biography: ''
    });
  };

  const handleCreateElection = () => {
    setLoading(true);
    // Simula chiamata API
    setTimeout(() => {
      const newElection = {
        ...electionForm,
        id: Date.now().toString(),
        status: 'draft',
        candidates: [],
        whitelist: []
      };
      setElections([...elections, newElection]);
      setLoading(false);
      closeModal();
    }, 1000);
  };

  const handleAddCandidate = () => {
    if (!selectedElection) return;

    setLoading(true);
    // Simula generazione indirizzo Bitcoin
    setTimeout(() => {
      const bitcoinAddress = `bc1q${Math.random().toString(36).substring(2, 15)}`;
      const newCandidate = {
        ...candidateForm,
        id: Date.now().toString(),
        bitcoinAddress,
        valueEncoding: selectedElection.candidates.length
      };

      const updatedElections = elections.map(e => 
        e.id === selectedElection.id 
          ? { ...e, candidates: [...e.candidates, newCandidate] }
          : e
      );

      setElections(updatedElections);
      setLoading(false);
      closeModal();
    }, 1500);
  };

  const handleCreateUser = () => {
    setLoading(true);
    setTimeout(() => {
      const newUser = {
        ...userForm,
        id: Date.now().toString(),
        status: 'active',
        isVerified: false
      };
      setUsers([...users, newUser]);
      setLoading(false);
      closeModal();
    }, 1000);
  };

  const validateTaxCode = (code) => {
    const regex = /^[A-Z]{6}[0-9]{2}[A-Z][0-9]{2}[A-Z][0-9]{3}[A-Z]$/i;
    return regex.test(code);
  };

  const activateElection = (electionId) => {
    const updatedElections = elections.map(e => 
      e.id === electionId ? { ...e, status: 'active' } : e
    );
    setElections(updatedElections);
  };

  const renderElectionsTab = () => (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-gray-800">Gestione Elezioni</h2>
        <button
          onClick={() => openModal('createElection')}
          className="flex items-center px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
        >
          <Plus className="mr-2" size={20} />
          Nuova Elezione
        </button>
      </div>

      <div className="grid gap-6">
        {elections.map(election => (
          <div key={election.id} className="bg-white rounded-lg shadow-md p-6">
            <div className="flex justify-between items-start mb-4">
              <div>
                <h3 className="text-xl font-semibold text-gray-800">{election.title}</h3>
                <p className="text-gray-600 mt-1">{election.description}</p>
                <div className="flex items-center mt-2 space-x-4 text-sm text-gray-500">
                  <span className="flex items-center">
                    <Calendar size={16} className="mr-1" />
                    {election.startDate} - {election.endDate}
                  </span>
                  <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                    election.status === 'active' ? 'bg-green-100 text-green-800' :
                    election.status === 'draft' ? 'bg-gray-100 text-gray-800' :
                    'bg-blue-100 text-blue-800'
                  }`}>
                    {election.status.toUpperCase()}
                  </span>
                </div>
              </div>
              <div className="flex space-x-2">
                <button
                  onClick={() => openModal('editElection', election)}
                  className="p-2 text-gray-600 hover:bg-gray-100 rounded-lg"
                >
                  <Edit2 size={18} />
                </button>
                <button className="p-2 text-gray-600 hover:bg-gray-100 rounded-lg">
                  <Eye size={18} />
                </button>
                {election.status === 'draft' && (
                  <button
                    onClick={() => activateElection(election.id)}
                    className="px-3 py-1 bg-green-600 text-white rounded-lg hover:bg-green-700 text-sm"
                  >
                    Attiva
                  </button>
                )}
              </div>
            </div>

            <div className="grid grid-cols-3 gap-4 mt-4">
              <div className="bg-gray-50 p-3 rounded-lg">
                <div className="flex items-center justify-between">
                  <span className="text-gray-600 text-sm">Candidati</span>
                  <button
                    onClick={() => {
                      setSelectedElection(election);
                      openModal('addCandidate');
                    }}
                    className="text-blue-600 hover:text-blue-700"
                  >
                    <Plus size={16} />
                  </button>
                </div>
                <p className="text-2xl font-bold text-gray-800 mt-1">{election.candidates.length}</p>
              </div>
              <div className="bg-gray-50 p-3 rounded-lg">
                <div className="flex items-center justify-between">
                  <span className="text-gray-600 text-sm">Whitelist</span>
                  <button
                    onClick={() => {
                      setSelectedElection(election);
                      openModal('manageWhitelist');
                    }}
                    className="text-blue-600 hover:text-blue-700"
                  >
                    <UserPlus size={16} />
                  </button>
                </div>
                <p className="text-2xl font-bold text-gray-800 mt-1">{election.whitelist.length}</p>
              </div>
              <div className="bg-gray-50 p-3 rounded-lg">
                <div className="flex items-center">
                  <Bitcoin size={16} className="text-orange-500 mr-2" />
                  <span className="text-gray-600 text-sm">CoinJoin Trigger</span>
                </div>
                <p className="text-2xl font-bold text-gray-800 mt-1">{election.coinjoinTrigger}</p>
              </div>
            </div>

            {election.candidates.length > 0 && (
              <div className="mt-4 pt-4 border-t">
                <h4 className="text-sm font-medium text-gray-700 mb-2">Candidati:</h4>
                <div className="grid grid-cols-2 gap-2">
                  {election.candidates.map(candidate => (
                    <div key={candidate.id} className="flex items-center justify-between bg-gray-50 p-2 rounded">
                      <span className="text-sm">
                        {candidate.firstName} {candidate.lastName} ({candidate.party})
                      </span>
                      <div className="flex items-center">
                        <Key size={14} className="text-gray-400 mr-1" />
                        <span className="text-xs text-gray-500 font-mono">
                          {candidate.bitcoinAddress.substring(0, 10)}...
                        </span>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );

  const renderUsersTab = () => (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-gray-800">Gestione Utenti</h2>
        <div className="flex space-x-2">
          <button className="flex items-center px-4 py-2 bg-gray-100 text-gray-700 rounded-lg hover:bg-gray-200 transition-colors">
            <Upload className="mr-2" size={20} />
            Importa CSV
          </button>
          <button
            onClick={() => openModal('createUser')}
            className="flex items-center px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
          >
            <Plus className="mr-2" size={20} />
            Nuovo Utente
          </button>
        </div>
      </div>

      <div className="bg-white rounded-lg shadow-md overflow-hidden">
        <table className="w-full">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Nome
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Email
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Codice Fiscale
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Stato
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Verificato
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Azioni
              </th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {users.map(user => (
              <tr key={user.id}>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="text-sm font-medium text-gray-900">
                    {user.firstName} {user.lastName}
                  </div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="text-sm text-gray-500">{user.email}</div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="text-sm text-gray-500 font-mono">{user.taxCode}</div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                    user.status === 'active' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                  }`}>
                    {user.status}
                  </span>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  {user.isVerified ? (
                    <Check className="text-green-600" size={20} />
                  ) : (
                    <X className="text-gray-400" size={20} />
                  )}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                  <button className="text-indigo-600 hover:text-indigo-900 mr-2">
                    <Edit2 size={16} />
                  </button>
                  <button className="text-red-600 hover:text-red-900">
                    <Trash2 size={16} />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );

  const renderModal = () => {
    if (!showModal) return null;

    return (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
        <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto">
          <div className="p-6">
            {modalType === 'createElection' && (
              <>
                <h3 className="text-xl font-bold mb-4">Crea Nuova Elezione</h3>
                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Titolo *
                    </label>
                    <input
                      type="text"
                      value={electionForm.title}
                      onChange={(e) => setElectionForm({...electionForm, title: e.target.value})}
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="Es. Elezione Comunale 2025"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Descrizione
                    </label>
                    <textarea
                      value={electionForm.description}
                      onChange={(e) => setElectionForm({...electionForm, description: e.target.value})}
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                      rows="3"
                      placeholder="Descrizione dell'elezione..."
                    />
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Data Inizio *
                      </label>
                      <input
                        type="datetime-local"
                        value={electionForm.startDate}
                        onChange={(e) => setElectionForm({...electionForm, startDate: e.target.value})}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Data Fine *
                      </label>
                      <input
                        type="datetime-local"
                        value={electionForm.endDate}
                        onChange={(e) => setElectionForm({...electionForm, endDate: e.target.value})}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                      />
                    </div>
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Trigger CoinJoin
                      </label>
                      <input
                        type="number"
                        value={electionForm.coinjoinTrigger}
                        onChange={(e) => setElectionForm({...electionForm, coinjoinTrigger: parseInt(e.target.value)})}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                        min="1"
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Max Votanti
                      </label>
                      <input
                        type="number"
                        value={electionForm.maxVotersAllowed}
                        onChange={(e) => setElectionForm({...electionForm, maxVotersAllowed: e.target.value})}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                        placeholder="Lascia vuoto per illimitato"
                      />
                    </div>
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Metodo di Voto
                      </label>
                      <select
                        value={electionForm.votingMethod}
                        onChange={(e) => setElectionForm({...electionForm, votingMethod: e.target.value})}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                      >
                        <option value="single">Voto Singolo</option>
                        <option value="preferential">Voto Preferenziale</option>
                        <option value="ranked">Voto Classificato</option>
                      </select>
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Rete Blockchain
                      </label>
                      <select
                        value={electionForm.blockchainNetwork}
                        onChange={(e) => setElectionForm({...electionForm, blockchainNetwork: e.target.value})}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                      >
                        <option value="testnet">Testnet</option>
                        <option value="mainnet">Mainnet</option>
                        <option value="regtest">Regtest</option>
                      </select>
                    </div>
                  </div>

                  <div className="flex items-center">
                    <input
                      type="checkbox"
                      id="coinjoinEnabled"
                      checked={electionForm.coinjoinEnabled}
                      onChange={(e) => setElectionForm({...electionForm, coinjoinEnabled: e.target.checked})}
                      className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                    />
                    <label htmlFor="coinjoinEnabled" className="ml-2 block text-sm text-gray-900">
                      Abilita CoinJoin automatico
                    </label>
                  </div>
                </div>
              </>
            )}

            {modalType === 'addCandidate' && (
              <>
                <h3 className="text-xl font-bold mb-4">Aggiungi Candidato</h3>
                <div className="space-y-4">
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Nome *
                      </label>
                      <input
                        type="text"
                        value={candidateForm.firstName}
                        onChange={(e) => setCandidateForm({...candidateForm, firstName: e.target.value})}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Cognome *
                      </label>
                      <input
                        type="text"
                        value={candidateForm.lastName}
                        onChange={(e) => setCandidateForm({...candidateForm, lastName: e.target.value})}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                      />
                    </div>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Partito/Lista
                    </label>
                    <input
                      type="text"
                      value={candidateForm.party}
                      onChange={(e) => setCandidateForm({...candidateForm, party: e.target.value})}
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Biografia
                    </label>
                    <textarea
                      value={candidateForm.biography}
                      onChange={(e) => setCandidateForm({...candidateForm, biography: e.target.value})}
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                      rows="4"
                    />
                  </div>

                  <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
                    <div className="flex items-center">
                      <Bitcoin className="text-orange-500 mr-2" size={20} />
                      <span className="text-sm font-medium text-gray-700">
                        L'indirizzo Bitcoin sar generato automaticamente per questo candidato
                      </span>
                    </div>
                  </div>
                </div>
              </>
            )}

            {modalType === 'createUser' && (
              <>
                <h3 className="text-xl font-bold mb-4">Crea Nuovo Utente</h3>
                <div className="space-y-4">
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Nome *
                      </label>
                      <input
                        type="text"
                        value={userForm.firstName}
                        onChange={(e) => setUserForm({...userForm, firstName: e.target.value})}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Cognome *
                      </label>
                      <input
                        type="text"
                        value={userForm.lastName}
                        onChange={(e) => setUserForm({...userForm, lastName: e.target.value})}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                      />
                    </div>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Email *
                    </label>
                    <input
                      type="email"
                      value={userForm.email}
                      onChange={(e) => setUserForm({...userForm, email: e.target.value})}
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Codice Fiscale *
                    </label>
                    <input
                      type="text"
                      value={userForm.taxCode}
                      onChange={(e) => setUserForm({...userForm, taxCode: e.target.value.toUpperCase()})}
                      className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                        userForm.taxCode && !validateTaxCode(userForm.taxCode) 
                          ? 'border-red-300' 
                          : 'border-gray-300'
                      }`}
                      maxLength="16"
                      placeholder="RSSMRA85M01H501Z"
                    />
                    {userForm.taxCode && !validateTaxCode(userForm.taxCode) && (
                      <p className="mt-1 text-sm text-red-600">
                        Formato codice fiscale non valido
                      </p>
                    )}
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Data di Nascita
                      </label>
                      <input
                        type="date"
                        value={userForm.dateOfBirth}
                        onChange={(e) => setUserForm({...userForm, dateOfBirth: e.target.value})}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Telefono
                      </label>
                      <input
                        type="tel"
                        value={userForm.phoneNumber}
                        onChange={(e) => setUserForm({...userForm, phoneNumber: e.target.value})}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                      />
                    </div>
                  </div>
                </div>
              </>
            )}

            <div className="flex justify-end space-x-2 mt-6">
              <button
                onClick={closeModal}
                className="px-4 py-2 bg-gray-200 text-gray-800 rounded-lg hover:bg-gray-300 transition-colors"
              >
                Annulla
              </button>
              <button
                onClick={() => {
                  if (modalType === 'createElection') handleCreateElection();
                  else if (modalType === 'addCandidate') handleAddCandidate();
                  else if (modalType === 'createUser') handleCreateUser();
                }}
                disabled={loading}
                className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors disabled:opacity-50"
              >
                {loading ? 'Creazione...' : 'Conferma'}
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <div className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center">
              <Vote className="text-blue-600 mr-3" size={32} />
              <h1 className="text-2xl font-bold text-gray-900">
                E-Voting WabiSabi Admin
              </h1>
            </div>
            <div className="flex items-center space-x-4">
              <span className="text-sm text-gray-500">Admin: admin@evoting.local</span>
              <button className="text-gray-500 hover:text-gray-700">
                <Settings size={20} />
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="bg-white rounded-lg shadow mb-6">
          <div className="border-b border-gray-200">
            <nav className="-mb-px flex space-x-8 px-6">
              <button
                onClick={() => setActiveTab('elections')}
                className={`py-3 px-1 border-b-2 font-medium text-sm ${
                  activeTab === 'elections'
                    ? 'border-blue-500 text-blue-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                <div className="flex items-center">
                  <Vote className="mr-2" size={18} />
                  Elezioni
                </div>
              </button>
              <button
                onClick={() => setActiveTab('users')}
                className={`py-3 px-1 border-b-2 font-medium text-sm ${
                  activeTab === 'users'
                    ? 'border-blue-500 text-blue-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                <div className="flex items-center">
                  <Users className="mr-2" size={18} />
                  Utenti
                </div>
              </button>
              <button
                onClick={() => setActiveTab('monitoring')}
                className={`py-3 px-1 border-b-2 font-medium text-sm ${
                  activeTab === 'monitoring'
                    ? 'border-blue-500 text-blue-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                <div className="flex items-center">
                  <Activity className="mr-2" size={18} />
                  Monitoraggio
                </div>
              </button>
            </nav>
          </div>
          <div className="p-6">
            {activeTab === 'elections' && renderElectionsTab()}
            {activeTab === 'users' && renderUsersTab()}
            {activeTab === 'monitoring' && (
              <div className="text-center py-12">
                <Activity className="mx-auto text-gray-400 mb-4" size={48} />
                <p className="text-gray-500">Sistema di monitoraggio in tempo reale</p>
                <p className="text-sm text-gray-400 mt-2">Funzionalit in sviluppo</p>
              </div>
            )}
          </div>
        </div>
      </div>

      {renderModal()}
    </div>
  );
};

export default AdminDashboard;
